// @refresh state
import React, { useState, useEffect } from "react";
import AsyncStorage from "@react-native-community/async-storage";
import { StyleSheet, Text, View, YellowBox } from "react-native";
import { StatusBar } from "expo-status-bar";
import firebase from "firebase";

const firebaseConfig = {
  apiKey: "AIzaSyAO79Yq5jjQWEsH5Oa-BEfBhvmMsyNZRoo",
  authDomain: "chat-app-room-990d0.firebaseapp.com",
  projectId: "chat-app-room-990d0",
  storageBucket: "chat-app-room-990d0.appspot.com",
  messagingSenderId: "347067636467",
  appId: "1:347067636467:web:99d4118d6c25d7ca0ff586",
};
// Initialize Firebase
if (firebase.apps.length === 0) {
  firebase.initializeApp(firebaseConfig);
}

YellowBox.ignoreWarnings(["Setting a timer for a long period of time"]);

export default function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    readUser();
  }, []);

  const readUser = async () => {
    const user = await AsyncStorage.getItem("user");
    if (user) {
      setUser(JSON.parse(user));
    }
  };

  if (!user) {
    return <View></View>;
  }

  return (
    <View style={styles.container}>
      <Text>Open up App.js to start working on your app!</Text>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
});
